    def limpiar(self):
        self.spinBoxPTL.setValue(0)
        self.spinBoxPTW.setValue(0)
        self.spinBoxSPL.setValue(0)
        self.spinBoxSPW.setValue(0)
        self.lineEditSpecies.clear()
        self.textEditRespuesta.clear()
        self.textEditRespuesta.setText("{}")

    def aceptar(self):
        sepal_length = self.spinBoxSPL.value()
        sepal_width = self.spinBoxSPW.value()
        petal_length = self.spinBoxPTL.value()
        petal_width = self.spinBoxPTW.value()
        species = self.lineEditSpecies.text()
        data = {
            'sepal_length': sepal_length,
            'sepal_width': sepal_width,
            'petal_length': petal_length,
            'petal_width': petal_width,
            'species': species
        }
        iris = RestIris()
        response = json.dumps(iris.post_data(data), indent=4)
        self.textEditRespuesta.setText(response)


